import React, { useState } from 'react';
import { Chessboard } from 'react-chessboard';
import Chess, { Square } from 'chess.js';
import { getPieceStyle, PieceStyle } from './style';
import MoveHistory from './history';
import parsePgn, { PgnObject } from '../../../utils/pgn/pgnParser';
import './lionChessboard.scss';


/**
 * This interface contains all the props for the 'LionChessboard' component.
 */
interface LionChessboardProps {
    /** The FEN position of the board */
    fen?: string;
    /** Whether only legal moves should be allowed */
    legalMoves?: boolean;
    /** The style of the pieces */
    pieceStyle?: PieceStyle;
    /** Whether to show the move history */
    showHistory?: boolean;
    /** Whether to show the 'next move' and 'previous move' buttons for the move history */
    showHistoryButtons?: boolean;
    /** An optional PGN game */
    pgn?: string;
    /** The size of the board in pixels */
    size?: number;
}

/**
 * This component displays a chessboard. For info about the props, refer to the
 * interface 'LionCHessboardProps'.
 * @param {LionChessboardProps} props The props for the chessboard
 * @returns The JSX that display the chessboard
 */
const LionChessboard: React.FC<LionChessboardProps> = ({
        legalMoves = true,
        fen = 'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1',
        pieceStyle = 'fresca',
        showHistory = false,
        showHistoryButtons = false,
        pgn = '',
        size = 500,
    }: LionChessboardProps) => {

    // @ts-ignore
    const [game, setGame] = useState( Chess(fen) );
    const [fenHistory, ] = useState< Array<string> >([]);
    const [gamePgn, setGamePgn] = useState<PgnObject>( parsePgn(pgn) )
    const [selectedMoveIndex, setSelectedMoveIndex] = useState<number>(-1);

    console.log(pgn);

    // legal moves only
    function onPieceDrop(square1: Square, square2: Square): boolean {
        let move = game.move({ from: square1, to: square2, promotion: 'q' });
        if (move !== null) {
            fenHistory.push(game.fen());
            setGame({...game})
            gamePgn.moves.push({ move: move.san });
            setGamePgn({...gamePgn})
            setSelectedMoveIndex(selectedMoveIndex + 1);
            return true;
        } else {
            return false;
        }
    }

    // gets the key of the currently selected move in the move history
    function getSelectedMove(): string {
        if (selectedMoveIndex % 2 === 0) {
            return selectedMoveIndex / 2 + 1 + 'w';
        } else {
            return (selectedMoveIndex - 1) / 2 + 1 + 'b';
        }
    }

    function selectMove(index: number): void {
        if (index >= 0 && index < gamePgn.moves.length) {
            setSelectedMoveIndex(index);
            // @ts-ignore
            setGame( Chess(fenHistory[index]) );
        }
    }

    // selects next move in the move history
    function selectNextMove(): void {
        selectMove(selectedMoveIndex + 1);
    }

    // selects previous move in the move history
    function selectPreviousMove(): void {
        selectMove(selectedMoveIndex - 1);
    }

    return (
        <div className="hstack gap-3">
            <div className="vstack gap-2" style={{width: size, height: size}}>
                <Chessboard
                onPieceDrop={ legalMoves? onPieceDrop : undefined }
                position={ game.fen() }
                customPieces={ getPieceStyle(pieceStyle) }
                customBoardStyle={{borderRadius: '7px'}}
                boardWidth={size}
                animationDuration={100}
                />
                {
                    showHistoryButtons &&
                    <div id="history-buttons-div" className="btn-group d-flex justify-content-between" role="group">
                        <button className="btn" onClick={selectPreviousMove}>
                            <i className="bi bi-caret-left-fill"></i>
                        </button>
                        <button className="btn" onClick={selectNextMove}>
                            <i className="bi bi-caret-right-fill"></i>
                        </button>
                    </div>
                }
            </div>
            {
                showHistory &&
                <div style={{
                    width: size * 0.65,
                    minHeight: size,
                    maxHeight: size,
                    overflowY: 'scroll',
                    overflowX: 'hidden',
                }}>
                    <MoveHistory
                    history={gamePgn.moves}
                    selectedMoveKey={getSelectedMove()}
                    onMoveClicked={index => selectMove(index)}
                    />
                </div>
            }
        </div>
    );
}

export default LionChessboard;
